  styleUrl: './input.component.sass'
})
export class InputComponent {
  /** Input variant */
  @Input()
  variant: 'text' | 'email' | 'password'| 'search' = 'text';

  /** Optional label */
  @Input()
  label: string = '';

  /** Optional placeholder */
  @Input()
  placeholder: string = '';

  // Uniquely generated ID for the instance
  inputId: string = `input-${uuidv4()}`;

  public get classes(): string[] {
    return ['labdt-input']
  }

  public get type(): string {
    switch (this.variant) {
      case 'text':
        return 'text';
      case 'email':
        return 'email';
      case 'password':
        return 'password';
      case 'search':
        return 'text';
      default:
        return 'text';
    }
  }
}

import { Component } from '@angular/core';
import { environment } from '../../../environments/environment';
import { AuthService } from '../../services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [],
  templateUrl: './login.component.html',
  styleUrl: './login.component.sass'
})
export class LoginComponent {

  constructor(private authService: AuthService, private router: Router) { }

  ngOnInit(): void {
    if (typeof window !== 'undefined' && typeof (window as any).google !== 'undefined') {
      // Initialize Google Identity with cliend_id and callback
      (window as any).google.accounts.id.initialize({
        client_id: environment.googleClientId,
        callback: this.handleCredentialResponse.bind(this),
        context: 'signin',
        ux_mode: 'popup',
        auto_select: false,
      });
    }

    this.renderGoogleLoginButton();
  }

  // Render Google's login button
  renderGoogleLoginButton(): void {
    if (typeof window !== 'undefined' && typeof (window as any).google !== 'undefined') {
      (window as any).google.accounts.id.renderButton(
        document.getElementById("google-login"),
        {
          type: "standard",
          shape: "pill",
          theme: "outline",
          text: "continue_with",
          size: "large",
          logo_alignment: "left",
          width: "360"
        }
      );
    }
  }

  // Callback function
  async handleCredentialResponse(response: any) {
    const token = response.credential;
    await this.authService.loginWithToken(token);
    if (this.authService.signedIn) {
      this.router.navigate(['/dashboard']);
    }
  }
}

import { Component, Input } from '@angular/core';
