import { CommonModule } from '@angular/common';
import { Component, EventEmitter, Input, Output } from '@angular/core';
import { v4 as uuidv4 } from 'uuid';

@Component({
  selector: 'app-input',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './input.component.html',
  styleUrl: './input.component.sass'
})
export class InputComponent {
  /** Input variant */
  @Input()
  variant: 'text' | 'email' | 'password'| 'search' = 'text';

  /** Optional label */
  @Input()
  label: string = '';

  /** Optional placeholder */
  @Input()
  placeholder: string = '';

  // Uniquely generated ID for the instance
  inputId: string = `input-${uuidv4()}`;

  public get classes(): string[] {
    return ['labdt-input']
  }

  public get type(): string {
    switch (this.variant) {
      case 'text':
        return 'text';
      case 'email':
        return 'email';
      case 'password':
        return 'password';
      case 'search':
        return 'text';
      default:
        return 'text';
    }
  }
}

import { Component } from '@angular/core';
import { environment } from '../../../environments/environment';

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [],
  templateUrl: './login.component.html',
  styleUrl: './login.component.sass'
})
export class LoginComponent {
  ngOnInit(): void {
    if (typeof window !== 'undefined') {
      // Initialize Google Identity with cliend_id and callback
      (window as any).google.accounts.id.initialize({
        client_id: environment.googleClientId,
        callback: this.handleCredentialResponse.bind(this),
        context: 'signin',
        ux_mode: 'popup',
        auto_select: false,
      });
    }

    this.renderGoogleLoginButton();
  }

  // Render Google's login button
  renderGoogleLoginButton(): void {
    if (typeof window !== 'undefined') {
      (window as any).google.accounts.id.renderButton(
        document.getElementById("google-login"),
        {
          type: "standard",
          shape: "pill",
          theme: "outline",
          text: "continue_with",
          size: "large",
          logo_alignment: "left",
          width: "360"
        }
      );
    }
  }

  // Callback function
  handleCredentialResponse(response: any) {
    const token = response.credential;
    console.log("Token ID:", token);
  }
}

import { Component, Input } from '@angular/core';
import { BaseModalComponent } from "../base-modal/base-modal.component";
import { CommonModule } from '@angular/common';
import { LogoComponent } from "../../components/logo/logo.component";
import { InputComponent } from "../../components/input/input.component";
import { ButtonComponent } from "../../components/button/button.component";
import { Subscription } from 'rxjs';
import { LoginModalService } from '../../services/login-modal.service';
import { LoginComponent } from "../../components/login/login.component";
import { RegisterModalService } from '../../services/register-modal.service';

@Component({
  selector: 'app-login-modal',
  standalone: true,
  imports: [CommonModule, BaseModalComponent, LogoComponent, InputComponent, ButtonComponent, LoginComponent],
  templateUrl: './login-modal.component.html',
  styleUrl: './login-modal.component.sass'
})
export class LoginModalComponent {
  /** Is the modal currently active? */
  @Input()
  isActive: boolean = false;

  private subscription: Subscription = new Subscription;

  constructor(
    private loginModalService: LoginModalService,
    private registerModalService: RegisterModalService,
  ) { }

  ngOnInit() {
    this.subscription = this.loginModalService.isActive$.subscribe(
      isActive => this.isActive = isActive
    )
  }

  ngOnDestroy() {
    if (this.subscription) {
      this.subscription.unsubscribe();
    }
  }

  closeModal() {
    this.loginModalService.close();
  }

  openRegisterModal() {
    this.closeModal();
    this.registerModalService.open();
  }
}

